docker run -it --rm --name certbot   -v  /$(pwd)/etc/ssl/cfhope.in.ua:/etc/letsencrypt   -v /$(pwd)/var/log/letsencrypt:/var/log/letsencrypt   -v /$(pwd)/web/letsencrypt/:/var/www  certbot/certbot:latest -t certonly   --quiet --agree-tos --renew-by-default  --email admin@cfhope.in.ua --webroot -w /var/www   -d cfhope.in.ua -d www.cfhope.in.ua
